from itertools import combinations

def acmTeam(topic):
    result=[]
    sum=0
    #calc permutation of idxTopic
    comb=combinations(topic,2)
    #calc over perm results
    for x in list(comb):
        for i ,s in enumerate(x[0]):
            sum+=(int(s)or int(x[1][i]))
        result.append(sum)
        sum=0
    res=max(result)
    return res,result.count(res)
    
    
    
